import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";import{o as n,c as i,e as s}from"./app.56b03e2c.js";var l="/JavaStudy/images/MySQL/MySQL-56.png",d="/JavaStudy/images/MySQL/MySQL-57.png",a="/JavaStudy/images/MySQL/MySQL-58.png",r="/JavaStudy/images/MySQL/MySQL-59.png",v="/JavaStudy/images/MySQL/MySQL-60.png",c="/JavaStudy/images/MySQL/MySQL-61.png",m="/JavaStudy/images/MySQL/MySQL-62.png",u="/JavaStudy/images/MySQL/MySQL-63.png";const t={},p=s('<h1 id="\u51FD\u6570" tabindex="-1"><a class="header-anchor" href="#\u51FD\u6570" aria-hidden="true">#</a> \u51FD\u6570</h1><h2 id="\u6982\u8FF0" tabindex="-1"><a class="header-anchor" href="#\u6982\u8FF0" aria-hidden="true">#</a> \u6982\u8FF0</h2><p><strong>\u51FD\u6570\u662F\u6307\u4E00\u6BB5\u53EF\u4EE5\u76F4\u63A5\u88AB\u53E6\u4E00\u6BB5\u7A0B\u5E8F\u8C03\u7528\u7684\u7A0B\u5E8F\u6216\u4EE3\u7801\u3002</strong></p><p>\u4E5F\u5C31\u610F\u5473\u7740\uFF0C\u8FD9\u4E00\u6BB5\u7A0B\u5E8F\u6216\u4EE3\u7801\u5728MySQL\u4E2D\u5DF2\u7ECF\u7ED9\u6211\u4EEC\u63D0\u4F9B\u4E86\uFF0C\u6211\u4EEC\u8981\u505A\u7684\u5C31\u662F\u5728\u9002\u5408\u7684\u4E1A\u52A1\u573A\u666F\u8C03\u7528\u5BF9\u5E94\u7684\u51FD\u6570\u5B8C\u6210\u5BF9\u5E94\u7684\u4E1A\u52A1\u9700\u6C42\u5373\u53EF\u3002\u90A3\u4E48\u51FD\u6570\u5230\u5E95\u5728\u54EA\u513F\u4F7F\u7528\u5462\uFF1F</p><p>\u6211\u4EEC\u5148\u770B\u4E24\u4E2A\u573A\u666F</p><p><img src="'+l+'" alt="1652149483383" loading="lazy"></p><p>1). \u5728\u4F01\u4E1A\u7684OA\u6216\u5176\u4ED6\u7684\u4EBA\u529B\u7CFB\u7EDF\u4E2D\uFF0C\u7ECF\u5E38\u4F1A\u63D0\u4F9B\u7684\u6709\u8FD9\u6837\u4E00\u4E2A\u529F\u80FD\uFF0C\u6BCF\u4E00\u4E2A\u5458\u5DE5\u767B\u5F55\u4E0A\u6765\u4E4B\u540E\u90FD\u80FD</p><p>\u591F\u770B\u5230\u5F53\u524D\u5458\u5DE5\u5165\u804C\u7684\u5929\u6570\u3002 \u800C\u5728\u6570\u636E\u5E93\u4E2D\uFF0C\u5B58\u50A8\u7684\u90FD\u662F\u5165\u804C\u65E5\u671F\uFF0C\u5982 2000-11-12\uFF0C\u90A3\u5982\u679C\u5FEB\u901F\u8BA1</p><p>\u7B97\u51FA\u5929\u6570\u5462\uFF1F</p><p>2). \u5728\u505A\u62A5\u8868\u8FD9\u7C7B\u7684\u4E1A\u52A1\u9700\u6C42\u4E2D,\u6211\u4EEC\u8981\u5C55\u793A\u51FA\u5B66\u5458\u7684\u5206\u6570\u7B49\u7EA7\u5206\u5E03\u3002\u800C\u5728\u6570\u636E\u5E93\u4E2D\uFF0C\u5B58\u50A8\u7684\u662F\u5B66\u751F\u7684</p><p>\u5206\u6570\u503C\uFF0C\u598298/75\uFF0C\u5982\u4F55\u5FEB\u901F\u5224\u5B9A\u5206\u6570\u7684\u7B49\u7EA7\u5462\uFF1F</p><p>\u5176\u5B9E\uFF0C\u4E0A\u8FF0\u7684\u8FD9\u4E00\u7C7B\u7684\u9700\u6C42\u5462\uFF0C\u6211\u4EEC\u901A\u8FC7MySQL\u4E2D\u7684\u51FD\u6570\u90FD\u53EF\u4EE5\u5F88\u65B9\u4FBF\u7684\u5B9E\u73B0 \u3002</p><p>MySQL\u4E2D\u7684\u51FD\u6570\u4E3B\u8981\u5206\u4E3A\u4EE5\u4E0B\u56DB\u7C7B\uFF1A<code> \u5B57\u7B26\u4E32\u51FD\u6570\u3001\u6570\u503C\u51FD\u6570\u3001\u65E5\u671F\u51FD\u6570\u3001\u6D41\u7A0B\u51FD\u6570\u3002</code></p><h2 id="\u5B57\u7B26\u4E32\u51FD\u6570" tabindex="-1"><a class="header-anchor" href="#\u5B57\u7B26\u4E32\u51FD\u6570" aria-hidden="true">#</a> \u5B57\u7B26\u4E32\u51FD\u6570</h2><p>MySQL\u4E2D\u5185\u7F6E\u4E86\u5F88\u591A\u5B57\u7B26\u4E32\u51FD\u6570\uFF0C\u5E38\u7528\u7684\u51E0\u4E2A\u5982\u4E0B\uFF1A</p><p><img src="'+d+`" alt="1652149780447" loading="lazy"></p><div class="language-mysql ext-mysql line-numbers-mode"><pre class="language-mysql"><code>-- concat(\u5B57\u7B26\u4E32\u62FC\u63A5)
select concat(&#39;Hello&#39; , &#39; MySQL&#39;);

-- lower\u5168\u90E8\u8F6C\u5C0F\u5199
select lower(&#39;Hello&#39;);

-- upper\u5168\u90E8\u8F6C\u5927\u5199
select upper(&#39;hello&#39;);

-- lpad\u5DE6\u586B\u5145
select lpad(&#39;he&#39;,5,&#39;--&#39;);

-- rpad\u53F3\u586B\u5145
select rpad(&#39;h2&#39;,5,&#39;--&#39;);

-- trim\u53BB\u9664\u7A7A\u683C
select trim(&#39;   Hello  World    &#39;);

-- substring\u622A\u53D6\u5B57\u7B26\u4E32
select substring(&#39;Hello Woeld&#39;,1,5);
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h4 id="\u6848\u4F8B" tabindex="-1"><a class="header-anchor" href="#\u6848\u4F8B" aria-hidden="true">#</a> \u6848\u4F8B</h4><p>\u7531\u4E8E\u4E1A\u52A1\u9700\u6C42\u53D8\u66F4\uFF0C\u4F01\u4E1A\u5458\u5DE5\u7684\u5DE5\u53F7\uFF0C\u7EDF\u4E00\u4E3A5\u4F4D\u6570\uFF0C\u76EE\u524D\u4E0D\u8DB35\u4F4D\u6570\u7684\u5168\u90E8\u5728\u524D\u9762\u88650\uFF0C\u6BD4\u5982\uFF1A1\u53F7\u5458\u5DE5\u7684\u5DE5\u53F7\u4E3A00001\u3002</p><div class="language-MySQL ext-MySQL line-numbers-mode"><pre class="language-MySQL"><code>update emplyee set workno = lpad(workno,5,&#39;0&#39;);
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>\u7ED3\u679C\uFF1A</p><p><img src="`+a+'" alt="1653637052279" loading="lazy"></p><h2 id="\u6570\u503C\u51FD\u6570" tabindex="-1"><a class="header-anchor" href="#\u6570\u503C\u51FD\u6570" aria-hidden="true">#</a> \u6570\u503C\u51FD\u6570</h2><p>\u5E38\u89C1\u6570\u503C\u51FD\u6570\u5982\u4E0B\uFF1A</p><p><img src="'+r+`" alt="1653637109714" loading="lazy"></p><div class="language-mysql ext-mysql line-numbers-mode"><pre class="language-mysql"><code>-- ceil(x)\u5411\u4E0A\u53D6\u6574
select ceil(1.1);

-- floor(x)\u5411\u4E0B\u53D6\u6574
select floor(1.9);

-- mod(x,y)\u8FD4\u56DEx/y\u7684\u6A21
select mod(7,4);

-- rand\u8FD4\u56DE0-1\u7684\u968F\u673A\u6570
select rand();

-- round(x,y)\u6C42\u53C2\u6570x\u7684\u56DB\u820D\u4E94\u5165\u7684\u503C\uFF0C\u4FDD\u7559y\u4F4D\u5C0F\u6570.\u4E0D\u5199\u6570\u503C\uFF08y\uFF09\uFF0C\u9ED8\u8BA4\u4E3A\u4FDD\u75590\u4F4D\u5C0F\u6570
select round(2.333333,2);
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h4 id="\u6848\u4F8B-1" tabindex="-1"><a class="header-anchor" href="#\u6848\u4F8B-1" aria-hidden="true">#</a> \u6848\u4F8B</h4><p>\u901A\u8FC7\u6570\u636E\u5E93\u7684\u51FD\u6570\uFF0C\u751F\u6210\u4E00\u4E2A\u516D\u4F4D\u6570\u7684\u968F\u673A\u9A8C\u8BC1\u7801</p><div class="language-mysql ext-mysql line-numbers-mode"><pre class="language-mysql"><code>select lpad(round(rand() *1000000),6,0) \u516D\u4F4D\u6570\u7684\u968F\u673A\u9A8C\u8BC1\u7801;
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>\u7ED3\u679C\uFF1A</p><p><img src="`+v+'" alt="1653637881833" loading="lazy"></p><h2 id="\u65E5\u671F\u51FD\u6570" tabindex="-1"><a class="header-anchor" href="#\u65E5\u671F\u51FD\u6570" aria-hidden="true">#</a> \u65E5\u671F\u51FD\u6570</h2><p>\u5E38\u89C1\u7684\u65E5\u671F\u51FD\u6570\u5982\u4E0B\uFF1A</p><p><img src="'+c+`" alt="1653638120456" loading="lazy"></p><div class="language-mysql ext-mysql line-numbers-mode"><pre class="language-mysql"><code>-- CURDATE()\u8FD4\u56DE\u5F53\u524D\u65E5\u671F
select curdate();

-- CURTIME()\u8FD4\u56DE\u5F53\u524D\u65F6\u95F4
select curtime();

-- NOW()\u8FD4\u56DE\u5F53\u524D\u65F6\u95F4\u548C\u65E5\u671F
select now();

-- YEAR(date)\u83B7\u53D6\u6307\u5B9Adate\u7684\u5E74\u4EFD
select year(now());

-- MONTH(date)\u83B7\u53D6\u6307\u5B9Adate\u7684\u6708\u4EFD
select month(now());

-- DAY(date)\u83B7\u53D6\u6307\u5B9Adate\u7684\u65E5\u671F
select day(now());

-- DATE_ADD(date,INTERVAL expr type)\u8FD4\u56DE\u4E00\u4E2A\u65E5\u671F/\u65F6\u95F4\u503C\u52A0\u4E0A\u4E00\u4E2A\u65F6\u95F4\u95F4\u9694expr\u540E\u7684\u65F6\u95F4\u503C
select date_add(now(),INTERVAL 70 day );

-- DATEDIFF(date1,date2)\u8FD4\u56DE\u8D77\u65F6\u65F6\u95F4date1\u548C\u7ED3\u675F\u65F6\u95F4date2\u4E4B\u95F4\u7684\u5929\u6570
SELECT DATEDIFF(&#39;2022-10-1&#39;,NOW()) \u4ECA\u5929\u5230\u56FD\u5E86\u8282\u7684\u5929\u6570;
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h4 id="\u6848\u4F8B-2" tabindex="-1"><a class="header-anchor" href="#\u6848\u4F8B-2" aria-hidden="true">#</a> \u6848\u4F8B</h4><p>\u8BE2\u6240\u6709\u5458\u5DE5\u7684\u5165\u804C\u5929\u6570\uFF0C\u5E76\u6839\u636E\u5165\u804C\u5929\u6570\u5012\u5E8F\u6392\u5E8F</p><div class="language-mysql ext-mysql line-numbers-mode"><pre class="language-mysql"><code>select  name,datediff(now(),entry) as  entrydays from emplyee order by entrydays desc ;
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p><img src="`+m+'" alt="1653640291428" loading="lazy"></p><h2 id="\u6D41\u7A0B\u51FD\u6570" tabindex="-1"><a class="header-anchor" href="#\u6D41\u7A0B\u51FD\u6570" aria-hidden="true">#</a> \u6D41\u7A0B\u51FD\u6570</h2><p>\u6D41\u7A0B\u51FD\u6570\u4E5F\u662F\u5F88\u5E38\u7528\u7684\u4E00\u7C7B\u51FD\u6570\uFF0C\u53EF\u4EE5\u5728SQL\u8BED\u53E5\u4E2D\u5B9E\u73B0\u6761\u4EF6\u7B5B\u9009\uFF0C\u4ECE\u800C\u63D0\u9AD8\u8BED\u53E5\u7684\u6548\u7387</p><p><img src="'+u+`" alt="1653640396670" loading="lazy"></p><div class="language-mysql ext-mysql line-numbers-mode"><pre class="language-mysql"><code>-- IF(value,t,f)
select if(false,&#39;ok&#39;,&#39;error&#39;);

-- IF NULL(value1,value2)
select ifnull(&#39;ok&#39;,&#39;default&#39;);

-- CASE WHEN[VAL1] THEN [res1]....ELSE[default] END


-- CASE[expr]WHEN[val1]THEN[res1]...ELSE[default] END
-- \u9700\u6C42\uFF1A\u67E5\u8BE2emp\u8868\u7684\u5458\u5DE5\u59D3\u540D\u548C\u5DE5\u4F5C\u5730\u5740\uFF08\u5317\u4EAC/\u4E0A\u6D77---&gt;\u4E00\u7EBF\u57CE\u5E02\uFF0C\u5176\u4ED6---&gt; \u4E8C\u7EBF\u57CE\u5E02\uFF09

select
    name,
    case workadress  when &#39;\u5317\u4EAC&#39; then &#39;\u4E00\u7EBF\u57CE\u5E02&#39; when &#39;\u4E0A\u6D77&#39; then &#39;\u4E00\u7EBF\u57CE\u5E02&#39; else &#39;\u4E8C\u7EBF\u57CE\u5E02&#39; end \u5DE5\u4F5C\u5730\u5740
from emplyee
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p>\u6848\u4F8B</p><p>\u7EDF\u8BA1\u73ED\u7EA7\u5404\u4E2A\u5B66\u5458\u7684\u6210\u7EE9\uFF0C\u5C55\u793A\u89C4\u5219\u5982\u4E0B\uFF1A</p><ul><li>\u5927\u4E8E\u7B49\u4E8E85\uFF0C\u5C55\u793A\u4F18\u79C0</li><li>\u5927\u4E8E\u7B49\u4E8E60\uFF0C\u5C55\u793A\u53CA\u683C</li><li>\u5426\u5219\uFF0C\u5C55\u793A\u4E0D\u53CA\u683C</li></ul><div class="language-mysql ext-mysql line-numbers-mode"><pre class="language-mysql"><code>select
    id,
    name,
    (case when math &gt;= 85 then &#39;\u4F18\u79C0&#39; when math &gt;=60 then &#39;\u53CA\u683C&#39; else &#39;\u4E0D\u53CA\u683C&#39; end )&#39;\u6570\u5B66\u6210\u7EE9&#39;,
    (case when english &gt;= 85 then &#39;\u4F18\u79C0&#39; when english &gt;=60 then &#39;\u53CA\u683C&#39; else &#39;\u4E0D\u53CA\u683C&#39; end )&#39;\u82F1\u8BED\u6210\u7EE9&#39;,
    (case when chinese &gt;= 85 then &#39;\u4F18\u79C0&#39; when chinese &gt;=60 then &#39;\u53CA\u683C&#39; else &#39;\u4E0D\u53CA\u683C&#39; end )&#39;\u8BED\u6587\u6210\u7EE9&#39;
from score;
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div>`,47),b=[p];function o(h,y){return n(),i("div",null,b)}var _=e(t,[["render",o],["__file","\u51FD\u6570.html.vue"]]);export{_ as default};
